import React, {useCallback, useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';

function loadTweets(callback){
  const xhr = new XMLHttpRequest() //retrieval of data  from xhr for
  //purpose of continually modifying loaded web page underlying concept
  //of AJAX Design.
  const method = 'GET'
  const url = "/tweets"
  const responseType = "json"
  xhr.responseType = responseType
  xhr.open(method,url) //open up this method with this url
  xhr.onload = function(){
    callback(xhr.response,xhr,status)
}

xhr.send() //trigger the request after i send it up

}



function App() {
  const [tweets, setTweets] = useState([])

  useEffect(() =>
  {
    // do my lookup
    loadTweets();
    const tweetItems = [{"content":123}, {"content": "Hello World"}]
    setTweets(tweetItems)
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <p>
          {tweets.map((tweet,index)=>{
            return <li>{tweet.content}</li>
          })}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
