import React, {useCallback, useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';

function loadTweets(callback){
  const xhr = new XMLHttpRequest() //retrieval of data  from xhr for
  //purpose of continually modifying loaded web page underlying concept
  //of AJAX Design.
  const method = 'GET'
  const url = "http://localhost:8000/api/tweets/"
  const responseType = "json"
  xhr.responseType = responseType
  xhr.open(method,url) //open up this method with this url
  xhr.onload = function(){
    callback(xhr.response,xhr.status)
}
xhr.onerror = function (e){
  console.log(e)
  callback({"message": "The request was an error!"}, 400)
}
xhr.send() //trigger the request after i send it up

}

function Tweet(props){
  const {tweet} = props
  const className = props.className ? props.className : 'col-10mx-auto cold-md-6'
  return <div className={className}>
    <p>{tweet.content}</p>

  </div>
}

function App() {
  const [tweets, setTweets] = useState([])


  useEffect(() =>
  {
    const myCallback = (response, status) => {
      console.log(response,status)
      if(status === 200){
        setTweets(response)

      }
      else{
        alert("There was an error")
      }

    }
    // do my lookup
    loadTweets(myCallback);

  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <p>
          {tweets.map((item,index)=>{
            return <Tweet tweet={item} key={index} />
          })}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
